dc = QuantumCircuit(5,3)

## Encode your bits here !
## Input bits are 1 and 0

dc.x(0) ### A =1
#dc.x(1) #### B = 0
####################

dc.barrier()

dc.x(1)
dc.ccx(0,1,2)


dc.x(0)
dc.x(1)
dc.ccx(0,1,3)

dc.measure(2,2)
dc.measure(3,0)

dc.x(2)
dc.x(3)
dc.ccx(2,3,4)
dc.measure(4,1)

dc.barrier()
dc.draw()

plot_histogram(execute(dc, Aer.get_backend('qasm_simulator')).result().get_counts())